changeSet.sql:
	CREATE TABLE CARGO(
	ID_CARGO BIGINT NOT NULL AUTO_INCREMENT,
	ID_ACESSO BIGINT,
	DESCRICAO_CARGO VARCHAR(2000),
	PRIMARY KEY (ID_CARGO),
FOREIGN KEY (ID_ACESSO) REFERENCES ACESSO(ID_ACESSO) ON UPDATE CASCADE ON DELETE CASCADE
); 
